import React from "react"
import ReactDOM from "react-dom"
import './css/style.css'


class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            exText: "example",
            userData: ""
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({ userData: event.target.value })
    }

    handleSubmit(event) {
        alert("Your data: " + this.state.userData)
        event.preventDefault()
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <h1>WRITE YOUR DATA</h1>
                    <input type="text" placeholder={this.state.exText} value={this.state.userData} onChange={this.handleChange} ></input>
                    <input type="submit" value="send" />
                </form>
            </div>
        )
    }
}



const root = ReactDOM.createRoot(document.getElementById("root"));

root.render(<App />)
