import React from "react";

class HelpHover extends React.Component{
    render(){
        return(
            <div className="help">
                <span className="help-alert"></span>
                <span className="question-mark">?</span>
                <div className="message">
                    <span className="message-title">if you have question</span>
                    <span className="message-text">
                    <p>Telegram - <a href="https://t.me/mobkukariku" >@mobkukariku</a></p>
                    <p>Instagram - <a href="https://www.instagram.com/damiirus/">@damiirus</a></p>
                    </span>
                </div>
            </div>
        )
    }
    

}



export default HelpHover
