import React from "react";

class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            xCoor: "",
            yCoor: ""
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({ xCoor: event.target.value, yCoor:event.target.value })
    }

    handleSubmit(event) {
        let sum = parseInt(this.state.xCoor)+parseInt(int.state.yCoor);
        alert("Your data: " + sum)
        event.preventDefault()
    }


    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit}>
                    <h1>WRITE YOUR DATA</h1>
                    <input type="text" placeholder={"X"} value={this.state.xCoor} onChange={this.handleChange} ></input>
                    <input type="text" placeholder={"Y"} value={this.state.yCoor} onChange={this.handleChange} ></input>
                    <input type="submit" value="send" onClick={this.handleSubmit}/>
                </form>
            </div>
        )
    }
}

export default App
