import React from "react";

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            FirstCoor: "",
            SecondCoor: ""
        };
        this.handleChangeX = this.handleChangeX.bind(this);
        this.handleChangeY = this.handleChangeY.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    finalCoor(firstCoor, secondCoor) {
        const p = Math.PI / 180;
        let answerX = 0;
        let answerZ = 0;
        let a = firstCoor.split(" ");
        let b = secondCoor.split(" ");
        if (a.length === 3 && b.length === 3) {
            let x1 = parseFloat(a[0]);
            let z1 = parseFloat(a[1]);
            let a1 = parseFloat(a[2]);
            let x2 = parseFloat(b[0]);
            let z2 = parseFloat(b[1]);
            let a2 = parseFloat(b[2]);
            if (Math.abs(a1 - a2) < 1) {
                alert('Углы не могут быть равны.');
            } else if (a1 > 360 || a2 > 360) {
                alert('Углы не могут быть больше 360 градусов.');
            } else {
                function cot(x) {
                    return 1 / Math.tan(x);
                }
                switch (Math.round(a1)) {
                    case -180:
                    case 0:
                    case 180:
                        answerX = Math.round(x1);
                        answerZ = Math.round(cot(-a2 * p) * x1 - (x2 * cot(-a2 * p) - z2));
                        break;
                    case -90:
                    case 90:
                        answerZ = Math.round(z1);
                        answerX = Math.round((x2 * cot(-a2 * p) - z2 + z1) / cot(-a2 * p));
                        break;
                    default:
                        console.log('че смотришь, дракона убивай');
                }
                switch (Math.round(a2)) {
                    case -180:
                    case 0:
                    case 180:
                        answerX = Math.round(x2);
                        answerZ = Math.round(cot(-a1 * p) * x2 - (x1 * cot(-a1 * p) - z1));
                        break;
                    case -90:
                    case 90:
                        answerZ = Math.round(z2);
                        answerX = Math.round((x1 * cot(-a1 * p) - z1 + z2) / cot(-a1 * p));
                        break;
                    default:
                        console.log('я сказал крепость ищи!!!');
                        answerX = Math.round(((x1 * cot(-a1 * p) - z1) - (x2 * cot(-a2 * p) - z2)) / (cot(-a1 * p) - cot(-a2 * p)));
                        answerZ = Math.round(cot(-a1 * p) * answerX - (x1 * cot(-a1 * p) - z1));
                }
            }
        }
        return { answerX, answerZ };
    }

    handleChangeX(event) {
        this.setState({ FirstCoor: event.target.value });
    }

    handleChangeY(event) {
        this.setState({ SecondCoor: event.target.value });
    }

    handleSubmit(event) {
        const { FirstCoor, SecondCoor } = this.state;
        const { answerX, answerZ } = this.finalCoor(FirstCoor, SecondCoor);
        alert("Coordinates: " + answerX + " " + answerZ);
        event.preventDefault();
    }

    render() {
        return (
            <div className="coordForm">
                <form onSubmit={this.handleSubmit}>
                    <h1 className="coordForm_title">Write your coordinates</h1>
                    <div className="coordForm_TextPlaces">
                    <input type="text" placeholder={"First"} value={this.state.FirstCoor} onChange={this.handleChangeX} />
                    <input type="text" placeholder={"Second"} value={this.state.SecondCoor} onChange={this.handleChangeY} />
                    </div>
                    <div className="coordForm_btn"><input type="submit" value="Send" /></div>
                </form>
            </div>
        );
    }
}

export default App;
