import React from "react"
import Header from "./components/Header";


class App extends React.Component {
    constructor(props){
        super(props)
        this.state={
            name: "Damir",
            age: 18,
            userData: ""
        }
        this.inputClick = this.inputClick.bind(this)
    }


    render() {
        return (
            <div className="name">
                <Header title="damir"></Header>
                <h1>Hello, {this.state.name}</h1>
                <h2>{this.state.userData}</h2>
                <p>
                    <input className="inp" placeholder="999" onClick={this.inputClick} onChange={event => this.setState({userData: event.target.value})}></input>
                </p>
                <h1>
                    {this.state.age==18 ? "YES" : "NO"}
                </h1>

            </div>
        )
    }
    inputClick() {
        this.setState({
            name: "Damir4ik",
            age: 19
        })
        console.log("Clicked")
    }
}


export default App
